for(var products=document.querySelectorAll(".product"),modal=document.querySelector(".modal"),modalButton=modal.querySelector(".modal__button"),overlay=document.querySelector(".overlay"),i=0;i<products.length;i++){var cart=products[i].querySelector(".product__cart");cart.addEventListener("click",function(e){e.preventDefault(),modal.classList.add("modal--show"),overlay.classList.add("overlay--show")})}var closeModal=function(e){e.preventDefault(),modal.classList.remove("modal--show"),overlay.classList.remove("overlay--show")};modalButton.addEventListener("click",closeModal),overlay.addEventListener("click",closeModal);